// If method is having without arguement, we will get same value

public class Area {

	public static void areaOfRect() {
		int length = 5, breadth = 4, area;
		area = length * breadth;
		System.out.println("Area of reactangle :" + area);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		areaOfRect();
		areaOfRect();
		areaOfRect();
	}

}