public class AreaWithArgu{
	public static void areaOfSq(int side) {
		int area_s;
		area_s=side*side;
		System.out.println(area_s);
	}
	public static void main(String args[]) {
		areaOfSq(3);
		areaOfSq(5);
		areaOfSq(6);
	}
}
